        int last=0,left=0;
        for(int k=i,tk;k<j;k=tk)
        {
            tk=k+1;
            while(tk<j&&shit[tk].one==shit[k].one&&shit[tk].two==shit[k].two){
                tk++;
            }
            int now=tk-k;
            SHIT tmp1,tmp2;
            tmp1.one=shit[k].one-1;
            tmp1.two=shit[k].two-1;
            if(mp.count(tmp1)){
                ans-=now;
                last=shit[i].one;
                left=now;
