struct point {
	int x, y, num;
	bool operator<(const point &p)const {
		return x + y != p.x + p.y ? x+y < p.x+p.y : x<p.x;
	}
}P[101000], U[101000];
int A[101000], B[101000], res, D[101000][2], first = 1;
vector<int>G[101000];
map<pii, int>Map;
int Get(int x, int y) {
	if (Map.count({ x,y }))return Map[{x, y}];
	return 0;
}
bool Go() {
	int i;
	if (first) {
		first = 0;
		for (i = 1; i < m; i++) {
			int x = U[i].x, y = U[i].y;
			res += max(Get(x, y), Get(x + 1, y - 1));
		}
		return true;
	}
	for (i = 0; i <= m + 1; i++) {
		D[i][0] = D[i][1] = 1e9;
	}
	D[0][0] = 0;
	for (i = 1; i <= m; i++) {
		int x = U[i].x, y = U[i].y;
		int ck1 = 0, ck2 = 0, ck3 = 0;
		if (Get(x-1,y-1)) ck1 = 1;
		if (Get(x-1,y)) ck2 = 1;
		if (Get(x,y-1)) ck3 = 1;
		if (ck1) {
			D[i][0] = min(D[i][0], D[i - 1][0] + Get(x, y));
		}
		if (ck2) {
			D[i][1] = min(D[i][1], D[i - 1][0] + Get(x, y));
		}
		if (ck2 &&ck3) {
			D[i][0] = min(D[i][0], D[i - 1][0] + Get(x, y) * 2);
		}
		if (i != 1) {
			D[i][1] = min(D[i][1], min(D[i - 1][0], D[i - 1][1]) + max(Get(x - 1, y + 1), Get(x, y)));
		}
		if (ck1 || ck3) {
			D[i][0] = min(D[i][0], D[i][1] + Get(x, y));
		}
	}
	if (D[m][0] > 1e8)return false;
	res += D[m][0];
	return true;
}
