    while (j < nr && a[j] + b[j] == a[i] + b[i])
        j ++;
    ///
    if (i == 1)
    {
        for (int k=1; k<j; k++)
            ans += max (sz[k], sz[k + 1]);
    }
    else
    {
        int cm = 0;
        for (int k=i; k<=j; k++)
        {
            if (both1[k])
            {
                ans += sz[k];
                if (may[k]) cm = min (cm, sz[k]);
                else cm = 0;
                continue;
            }
            if (sz[k] > cm) ans += sz[k] - cm;
            ans += sz[k];
            if (may[k]) cm = sz[k];
            else cm = 0;
        }
