bool cmp (int i, int j)
{
    if (A[i] + B[i] == A[j] + B[j]) return (A[i] < A[j]);
    return (A[i] + B[i] < A[j] + B[j]);
}

int findPair (int x, int y)
{
    int p = 1, u = nr, mij;
    while (p <= u)
    {
        mij = (p + u) >> 1;
        if (a[mij] == x && b[mij] == y) return mij;
        if (a[mij] + b[mij] < x + y || (a[mij] + b[mij] == x + y && a[mij] < x)) p = mij + 1;
        else u = mij - 1;
    }
    return 0;
}

int main ()
{
