    i = j;
}
for (int i=1; i<=nr; i++)
    may[i] = (findPair (a[i] + 1, b[i] - 1) != 0),
    both1[i] = (findPair (a[i] - 1, b[i] - 1) != 0);
if (!may[1])
{
    printf ("-1\n");
    return 0;
}
for (int i=1; i<=nr; i++)
