void ng() {
        printf("-1\n");
        exit(0);
}

int main() {
        int n;
        scanf("%d", &n);
        vector<set<int>> ab(n), ba(n);
        vector<pair<int, int>> p;
        for (int i = 0; i < n; i ++) {
                int a, b;
                scanf("%d%d", &a, &b);
                p.emplace_back(a, b);
                ab[a].insert(b);
                ba[b].insert(a);
        }
        if (p[0].first != 0 || p[1].second != 0) ng();
        if (p[0].second != p[1].first) ng();
        for (int i = 2; i < n; i ++) {
                int a, b;
                tie(a, b) = p[i];
                if (a == 0 || b == 0) ng();
                if (ab[a - 1].count(b + 1) == 0 && ab[a - 1].count(b) == 0 && ab[a - 1].count(b - 1)) ng();
                if (ba[b - 1].count(a + 1) == 0 && ba[b - 1].count(a) == 0 && ba[b - 1].count(a - 1)) ng();
        }
        assert(false);
        return 0;
}

