#include <bits/stdc++.h>

using namespace std;

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n;
  cin >> n;
  vector<int> a(n), b(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i] >> b[i];
  }
  if (a[0] != 0 || b[1] != 0 || a[1] == 0 || a[1] != b[0]) {
    cout << -1 << '\n';
    return 0;
  }
  int d = a[1];
  vector<int> was(d + 1, 0);
  for (int i = 0; i < n; i++) {
    if (a[i] + b[i] < d) {
      cout << -1 << '\n';
      return 0;
    }
    if (a[i] + b[i] == d) {
      was[a[i]] = 1;
    }
  }
  if (*min_element(was.begin(), was.end()) == 0) {
    cout << -1 << '\n';
    return 0;
  }
  set<pair<int, int>> s;
  for (int i = 0; i < n; i++) {
    s.emplace(a[i], b[i]);
  }
  int ans = 0;
  for (int i = 0; i < n; i++) {
    if (s.find({a[i] - 1, b[i] - 1}) != s.end()) {
      ans += 2;
    } else {
      if (a[i] > 0) {
        if (s.find({a[i] - 1, b[i] + 1}) != s.end()) ans += 1; else
        if (s.find({a[i] - 1, b[i]}) != s.end()) ans += 2;
        else {
          cout << -1 << '\n';
          return 0;
        }
      }
      if (b[i] > 0) {
        if (s.find({a[i] + 1, b[i] - 1}) != s.end()) ans += 1; else
        if (s.find({a[i], b[i] - 1}) != s.end()) ans += 2;
        else {
          cout << -1 << '\n';
          return 0;
        }
      }
    }
  }
  assert(ans % 2 == 0);
  cout << ans / 2 << '\n';
  return 0;
}

#include<bits/stdc++.h>
