#define N 100010
int n;
int a[N], b[N];
map<PII, set<int> > A;

void end() {
	puts ("-1");
	exit(0);
}

int path[N], L;
bool v[N];

vector<int> c[N];
// c[i] = connect with path[i] and path[i+1]
int sc[N];

int main() {
  cin >> n;
  for (int i = 0; i < n; i ++) {
  	cin >> a[i] >> b[i];
  	A[mp(a[i], b[i])].insert(i);
  }
  if (a[0] != 0 || b[1] != 0 || a[1] != b[0]) end();
  L = a[1];
  path[0] = 0;
  path[L] = 1;
  for (int i = 1; i < L; i++)
  	path[i] = -1;
  for (int i = 2; i < n; i++)
  	if (a[i] + b[i] == L) {
  		if (path[a[i]] == -1) {
  		   path[a[i]] = i;
  		   v[i] = true;
  		}
  	}
  for (int i = 2; i < n; i ++) {
    if (a[i] == 0 || b[i] == 0) end();
    if (a[i] + b[i] < L) end();
  }
  int S = 0;
  S += L; // path
  for (int i = 2; i < n; i ++) {
    if (a[i] + b[i] == L) {
      S += 2;
    } else
    if (a[i] + b[i] == L+1) {
      S += 2;
      c[a[i]-1].pb(i);
    } else {
      if (A.find(mp(a[i]-1, b[i]-1)) != A.end()) {
        S ++;
      } else {
        end();
      }
    }
  }
  for (int i = 0; i < L; i ++) {
    sc[i] = (int) c[i].size();
  }
  for (int i = 0; i < L-1; i ++) {
    int T = min(sc[i], sc[i+1]);
    S -= T;
  }
  cout << S << endl;
  return 0;
}

