const ll INF=1e18;
int main()
{
	int n;
	cin>>n;
	ll a[20000];
	for(int i=0; i<n; i++) cin>>a[i];
	ll p10=1;
	int ans=0;
	for(int i=0; i<=16; i++){
		ll dp[2][20001][10];
		for(int j=0; j<=n; j++){
			for(int k=0; k<10; k++){
				dp[0][j][k]=INF; dp[1][j][k]=-1;
			}
		}
		dp[0][0][0]=0;
		dp[1][0][0]=0;
		for(int j=0; j<n; j++){
			for(int k=0; k<10; k++){
                dp[0][j+1][k]=min(dp[0][j+1][k], dp[0][j][k]);
				dp[1][j+1][k]=max(dp[1][j+1][k], dp[1][j][k]);
				if(dp[1][j][k]==-1) continue;
				ll a0=p10*k+dp[0][j][k]+a[j];
				ll a1=p10*k+dp[1][j][k]+a[j];
				dp[0][j+1][a0/p10%10]=min(a0%p10, dp[0][j+1][a0/p10%10]);
				dp[1][j+1][a0/p10%10]=max(a0%p10, dp[1][j+1][a0/p10%10]);
				dp[0][j+1][a1/p10%10]=min(a1%p10, dp[0][j+1][a1/p10%10]);
				dp[1][j+1][a1/p10%10]=max(a1%p10, dp[1][j+1][a1/p10%10]);
			}
		}
		for(int k=9; k>=0; k--){
			if(dp[1][n][k]>=0){
				ans+=k;
				break;
			}
		}
		p10*=10;
	}
	cout<<ans<<endl;
	return 0;
}

