int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n;
  cin >> n;
  vector<long long> a(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i];
  }
  long long total = accumulate(a.begin(), a.end(), 0LL);
  int ans = 0;
  long long p10 = 1;
  while (p10 <= total) {
    vector<long long> f(1, 0);
    for (long long x : a) {
      int sz = (int) f.size();
      f.resize(2 * sz);
      for (int i = 0; i < sz; i++) {
        f[i + sz] = (f[i] + x) % (10 * p10);
      }
      sort(f.begin(), f.end());
      int new_sz = 0;
      for (int i = 0; i < 2 * sz; i++) {
        if (i > 0 && i < 2 * sz - 1 && f[i + 1] - f[new_sz - 1] <= p10) {
          continue;
        }
        f[new_sz++] = f[i];
      }
      f.resize(new_sz);
    }
    ans += (int) (f.back() / p10);
    p10 *= 10;
  }
  cout << ans << '\n';
  return 0;
}

