int N; long long A[20002];

int main()
{
	scanf("%d", &N);
	for (int i = 0; i < N; i++) scanf("%lld", &A[i]);

	int ans = 0;
	for (long long p = 1, d = 0; d < 17; p *= 10, d++)
	{
		long long x = p * 10;
		for (int s = 9; s >= 0; s--) {
			vector<pair<long long, long long> > now;
			now.emplace_back(s * p, (s + 1) * p - 1);

			for (int i = 0; i < N; i++) {
				vector<pair<long long, long long> > nxt = now;
				for (auto &p : now) {
					long long s = (p.first - A[i] % x + x) % x;
					long long e = (p.second - A[i] % x + x) % x;
					if (s <= e) nxt.push_back({ s,e });
					else {
						nxt.push_back({ 0,e });
						nxt.push_back({ s,x - 1 });
					}
				}

				sort(nxt.begin(), nxt.end());
				long long s = nxt[0].first, e = nxt[0].second;
				now.clear();
				for (auto &p : nxt) {
					if (p.first <= e + 1) {
						e = p.second;
					}
					else {
						now.push_back({ s,e });
						s = p.first;
						e = p.second;
					}
				}
				now.push_back({ s,e });
			}

			if (now[0].first == 0) {
				ans += s;
				break;
			}
		}
	}
	printf("%d\n", ans);

	return 0;
}

