    for (int dig = 0; dig < 17; dig++) {
      step *= 10;
      set <long long> e;
      e.insert(0LL);
      long long mx = 0;
      for (int i = 1; i <= n; i++) {
        long long cur = s[i] % step;
        auto it = e.lower_bound(cur + 1);
        if (it == e.end()) {
          it = e.begin();
        }
        long long other = *it;
        long long diff = cur - other;
        if (diff < 0) {
          diff += step;
        }
        if (diff > mx) {
          mx = diff;
        }
      }
      ans[dig] = max(ans[dig], (int) (mx / (step / 10)));
    }
  }
  int res = 0;
  for (int dig = 0; dig < 17; dig++) {
    res += ans[dig];
  }
