	pr(ll l=0,ll r=0):l(l),r(r){}
}p[30],q[30];
bool operator<(pr a,pr b){return a.l==b.l?a.r<b.r:a.l<b.l;}
int M;
bool gao(){
	int i,C;
	ll l,r;
	for(i=1;i<=M;i++){
		if(!p[i].l||p[i].l>p[i].r)return 1;
	}
	sort(p+1,p+M+1);
	C=0;
	l=p[1].l;
	r=p[1].r;
	for(i=2;i<=M;i++){
		if(r<p[i].l-1){
			q[++C]=pr(l,r);
			l=p[i].l;
			r=p[i].r;
		}else
			r=max(r,p[i].r);
	}
	q[++C]=pr(l,r);
	for(i=1;i<=C;i++)p[i]=q[i];
	M=C;
	return 0;
}
