                }

                {
                int v = c * pw[p] + dp[c].s + x;
                int c1 = (v / pw[p]) % 10;
                int r1 = v % pw[p];
                dp2[c1].f = min(dp2[c1].f, r1);
                dp2[c1].s = max(dp2[c1].s, r1);
                }
            }   
            for (int c = 0; c < 10; ++c)
                dp[c] = dp2[c];                        
        }   

        for (int c = 9; c >= 0; --c) {
            if (dp[c].f != INF) {
                ans += c;
                break;
            }   
        }
    }   
    cout << ans << endl;
}

