typedef long long ll;
typedef pair<ll, ll> Pair;

vector<Pair> v, w;
vector<ll> A;
ll p10[20];

vector<Pair> best(vector<Pair> v)
{
    vector<Pair> ans;
    sort(v.begin(), v.end());

    ll l = -1, r = -1;
    for(auto it : v)
        if(it.first <= r)
            r = max(r, it.second);
        else
        {
            if(l != -1) ans.push_back({l, r});
            l = it.first;
            r = it.second;
        }
    ans.push_back({l, r});
    return ans;
}

bool solve(int p, int c)
{
    w.clear();
    w.push_back({p10[p] * c, p10[p] * (c+1) - 1});

    ll x, y;
    for(auto val : A)
    {
        v.clear();
        for(auto seg : w)
        {
            x = seg.first;
            y = seg.second;
            v.push_back({x, y});

            val %= p10[p+1];
            x -= val;
            y -= val;

            if(x<=0 && y>=0) return 1;

            if(x<0 && y<0) x += p10[p+1], y += p10[p+1];
            v.push_back({x, y});
        }

        w = best(v);
    }

    return 0;
}

