    dist[i] = 0;
    for (int l = 0; l < sz(st); ++l) {
      int v = st[l];
      for (int u : es[v]) {
        if (dist[u] > dist[v] + 1) {
          st.pb(u);
          dist[u] = dist[v] + 1;
        }
      }
    }
    if (sz(st) < n) {
      goto beg;
    }
  }
  break;
  }
  eprintf("n = %d\n", n);
  solve();
  if (res == -1 || res > m) {
    assert(0);
  }
}
