        int x, y;
        cin >> x >> y;
        a.insert({x, y});
        if (x == 0 || y == 0) {
            cout << "-1\n";
            return;
        }
    }

    map<int, int> cnt;
    int ans = n - 1;

    for (auto item : a) {
        if (abs(item.first - item.second) > dist) {
            cout << "-1\n";
            return;
        }
        int diff =  item.first + item.second - dist;
        if (diff < 0) {
            cout << "-1\n";
            return;
        }
        if (diff == 0) {
            cnt[item.first]++;
            continue;
        }
        if (diff == 1) {
            ++ans;
            continue;
        }
        if (a.find(make_pair(item.first  - 1, item.second - 1)) == a.end()) {
            cout << "-1\n";
            return;
        }
    }
    int sum = 0;
    for (auto item : cnt) {
        sum += item.second - 1;
    }

    for (int i = 1; i<=dist; ++i) {
        sum -= max(min(cnt[i], cnt[i - 1]) - 1, 0);
    }

    ans += sum;

    if (cnt.size() != dist + 1) {
        cout << "-1\n";
        return;
    }

    cout << ans << "\n";

