		//if (S.count(pii(p.first - 1, p.second))) { ans++; S.insert(p); S2.insert(p);  continue; }
		if (S.count(pii(p.first, p.second - 1)) && S.count(pii(p.first - 1, p.second))) { 
			S2.insert(p);  S.insert(p); 
			if (!S2.count(pii(p.first - 1, p.second + 1))) ans++;
			continue; 
		}
		no();
	}
}

