int L;
int did[101010],elim[101010];
int ok[101010];

set<pair<int,int>> S;
set<pair<int,int>> dup;
vector<pair<int,int>> V[202020];

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	FOR(i,N) {
		cin>>A[i]>>B[i];
		if(i>1) {
			if(dup.count({A[i],B[i]})) {
				did[i]=elim[i]=1;
			}
			dup.insert({A[i],B[i]});
		}
	}

	if(A[0]!=0) return _P("-1\n");
	if(B[1]!=0) return _P("-1\n");
	if(A[1]!=B[0]) return _P("-1\n");
	L=A[1];
	ok[0]=ok[L]=1;
	did[0]=did[1]=1;

	S.insert({0,L});
	S.insert({L,0});
	int ret=L;
	for(i=2;i<N;i++) {
		if(did[i]) continue;
		if(A[i]==0 || B[i]==0) return _P("-1\n");
		if(A[i]+B[i]<L) return _P("-1\n");
		if(A[i]+B[i]==L && ok[A[i]]==0) {
			did[i]=1;
			ok[A[i]]=1;
			S.insert({A[i],B[i]});
		}
		else {
			V[A[i]+B[i]].push_back({A[i],i});
		}
	}
	FOR(i,L+1) if(ok[i]==0) return _P("-1\n");
	for(i=L;i<=200000;i++) if(V[i].size()) {
		sort(ALL(V[i]));
		FOR(j,4) {
			FORR(r,V[i]) {
				x = r.second;
				if(did[x]) continue;
				if(S.count({A[x]-1,B[x]-1})) ret++;
				else if(S.count({A[x]-1,B[x]}) && S.count({A[x],B[x]-1})) ret+=2;
				else if(S.count({A[x]-1,B[x]+1}) && S.count({A[x],B[x]-1})) ret+=2;
				else if(S.count({A[x]-1,B[x]}) && S.count({A[x]+1,B[x]-1})) ret+=2;
				else if(S.count({A[x]-1,B[x]+1}) && S.count({A[x]+1,B[x]-1})) ret+=2;
				else {
					continue;
				}
				did[x]=1;
				S.insert({A[x],B[x]});
			}

			y=-1;

			FOR(x,V[i].size()) {
				if(x>0 && abs(V[i][x].first-V[i][x-1].first)>1) y=-1;

				if(did[V[i][x].second]) {
					if(y>=0 && y-x>1) {
						ret++;
						while(y<x) {
							did[V[i][y].second]=1;
							S.insert({A[V[i][y].second],B[V[i][y].second]});
							ret++;
						}
					}
					y=x;
				}
			}

			reverse(ALL(V[i]));
		}


	}

	FOR(x,N) if(elim[x]==1) {
		if(S.count({A[x]-1,B[x]-1})) ret++;
		else if(S.count({A[x]-1,B[x]}) && S.count({A[x],B[x]-1})) ret+=2;
		else if(S.count({A[x]-1,B[x]+1}) && S.count({A[x],B[x]-1})) ret+=2;
		else if(S.count({A[x]-1,B[x]}) && S.count({A[x]+1,B[x]-1})) ret+=2;
		else if(S.count({A[x]-1,B[x]+1}) && S.count({A[x]+1,B[x]-1})) ret+=2;
		else return _P("-1\n");
	}
	FOR(i,N) if(did[i]==0) return _P("-1\n");
	_P("%d\n",ret);


}


int main(int argc,char** argv){
	string s;int i;
	if(argc==1) ios::sync_with_stdio(false), cin.tie(0);
	FOR(i,argc-1) s+=argv[i+1],s+='\n';
	FOR(i,s.size()) ungetc(s[s.size()-1-i],stdin);
	solve(); return 0;
}

