using namespace std;

int main() {
  int N; scanf("%d", &N);
  static int A[100000], B[100000];
  static pair<int, int> ABtbl[100000];
  static pair<int, int> BAtbl[100000];
  for(int i = 0; i < N; ++i) {
    scanf("%d%d", &A[i], &B[i]);
    ABtbl[i] = make_pair(A[i], B[i]);
    BAtbl[i] = make_pair(B[i], A[i]);
  }
  if(A[0] != 0 || B[1] != 0 || A[1] == 0 || A[1] != B[0]) {
    printf("%d\n", -1);
    return 0;
  }
  sort(ABtbl, ABtbl+N);
  sort(BAtbl, BAtbl+N);
  for(int i = 0; i < N; ++i) {
    if(i != 0 &&
        lower_bound(ABtbl, ABtbl+N, make_pair(A[i]-1, B[i]-1))
        == upper_bound(ABtbl, ABtbl+N, make_pair(A[i]-1, B[i]+1))) {
      printf("%d\n", -1);
      return 0;
    }
    if(i != 1 &&
        lower_bound(BAtbl, BAtbl+N, make_pair(B[i]-1, A[i]-1))
        == upper_bound(BAtbl, BAtbl+N, make_pair(B[i]-1, A[i]+1))) {
      printf("%d\n", -1);
      return 0;
    }
  }
  int count = 2 * (N-1);
  for(int ii = 0; ii < N; ++ii) {
    if(ii > 0 && ABtbl[ii-1] == ABtbl[ii]) continue;
    int a = ABtbl[ii].first;
    int b = ABtbl[ii].second;
    if(lower_bound(ABtbl, ABtbl+N, make_pair(a-1, b+1))
        < upper_bound(ABtbl, ABtbl+N, make_pair(a-1, b+1))) {
      continue;
    }
    int have =
      upper_bound(ABtbl, ABtbl+N, make_pair(a, b))
        - lower_bound(ABtbl, ABtbl+N, make_pair(a, b));
    int rem = have;
    while(true) {
      int capa = have;
      if(lower_bound(ABtbl, ABtbl+N, make_pair(a-1, b-1))
          < upper_bound(ABtbl, ABtbl+N, make_pair(a-1, b-1))) {
        // fprintf(stderr, "(%d, %d) -- %d recyclable edges to (%d, %d)\n",
        //     a, b, rem, a-1, b-1);
        count -= rem;
        capa -= rem;
      }
      ++a;
      --b;
      have =
        upper_bound(ABtbl, ABtbl+N, make_pair(a, b))
          - lower_bound(ABtbl, ABtbl+N, make_pair(a, b));
      if(have == 0) break;
      // fprintf(stderr, "(%d, %d) -- %d recyclable edges to (%d, %d)\n",
      //     a-1, b+1, min(capa, have), a, b);
      count -= min(capa, have);
      rem = have - min(capa, have);
    }
  }
  printf("%d\n", count);
  return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef long long LL;
typedef pair<int,int> PII;

bool solve(vector<int> &A, vector<int>& B, int &res) {
	if(A[0] != 0) return false;
	if(B[1] != 0) return false;
	if(A[1] != B[0]) return false;
	int d = A[1];

	int N = A.size();
	vector<int> minimcnt(d+1, 0);
	for(int i=0; i<N; i++) {
		if(A[i] + B[i] == d) {
			minimcnt[A[i]] ++;
		}
		if(A[i] + B[i] < d) return false;
	}
	for(int i=0; i<=d; i++) {
		if(minimcnt[i] == 0) return false;
	} 
	if(minimcnt[0] > 1) return false;
	if(minimcnt[d] > 1) return false;

	map<int, vector<int> > data;

	for(int i=0; i<N; i++) {
		int dist = B[i] - A[i];
		int sum = A[i] + B[i];
		if(abs(dist) > d) return false;

		if(sum>d)
			data[dist].push_back(sum);
	}

	int ans = 0;

	for(int i=0; i<d; i++) {
		ans += max(minimcnt[i], minimcnt[i+1]);
	}
//	cout << ans << endl;
	vector<int> cnt(d+1, 0);
	for(int k=-d; k<=d; k++) {
	//	cout << data[k].size() << endl;
		if(data[k].size() == 0) continue;
		/*
		if(abs(d - k) % 2 == 1) {
			ans += 2;
		}*/
//		ans += data[k].
		sort(data[k].begin(), data[k].end());
		int init = data[k][0];
		if( (init - d) > 2) return false;


		for(int i=1; i<data[k].size(); i++) {
			if(data[k][i] - data[k][i-1] > 2) return false;
		}
		for(int i=0; i<data[k].size(); i++) {
			if(data[k][i] == init) {
				//ans += 2;
				if(abs(d-k)%2==0) ans++;
				else {
					cnt[(k+d)/2]++;
//					cout << "    " << k << " " << d << endl;
				}
			}
			else ans += 1;
		}
	}
	for(int i=0; i<cnt.size()-1; i++) {
		ans += max(cnt[i], cnt[i+1]);
