int N;
int A[100010], B[100010];
bool hige[100010];

map<pair<int, int>, int> cnt;

void dmp() {
  puts("-1");
  exit(0);
}

int main() {
  scanf("%d", &N);
  rep (i, N) {
    scanf("%d%d", &A[i], &B[i]);

    if ((A[i] == 0) != (i == 0)) dmp();
    if ((B[i] == 0) != (i == 1)) dmp();
  }

  int D = B[0];
  if (A[1] != D) dmp();

  rep (i, N) cnt[mp(A[i], B[i])] += 1;
  rep (i, N) hige[i] = cnt.count(mp(A[i] - 1, B[i] - 1));

  map<int, map<int, int>> taba;
  rep (i, N) {
    if (hige[i]) continue;
    taba[A[i] + B[i]][A[i]] += 1;
  }

  for (int d = 0; d < D; ++d) {
    if (taba.count(d)) dmp();
  }

  int ans = N - 2;  //count(hige, hige + N, true);

  for (auto &i : taba) {
    map<int, int> &m = i.second;
    int d = i.first;

    for (const auto &j : m) {
      int a = j.first, b = d - a, c = j.second;

      int prv_c = cnt[mp(a - 1, b + 1)];
      int nxt_c = cnt[mp(a + 1, b - 1)];
      // printf("%d %d: %d %d %d\n", a, b, prv_c, c, nxt_c);

      int bgn = max(0, c - prv_c);
      int end = max(0, c - nxt_c);

      if (bgn > 0) {
        // printf(" %d\n", bgn);
        ans += bgn;
        if (a > 0 && cnt[mp(a - 1, b)] == 0) {
          //printf("%d %d\n", a, b);
          dmp();
        }
      }
      if (end > 0) {
        if (b > 0 && cnt[mp(a, b - 1)] == 0) {
          //          printf("%d %d\n", a, b);
          dmp();
        }
      }
    }
  }

  printf("%d\n", ans);

  return 0;
}

