bool find(pair<int,int> *P,int n,int a,int b)
{
  int k=lower_bound(P,P+n,make_pair(a,b))-P;
  return k<n&&P[k]==make_pair(a,b);
}

int main()
{
  int n;
  scanf("%d",&n);
  static int A[100000],B[100000];
  for(int i=0;i<n;i++){
    scanf("%d%d",A+i,B+i);
  }
  if(A[0]!=0||B[1]!=0||A[1]!=B[0]){
    puts("-1");
    return 0;
  }
  int L=A[1];
  int C=0;
  static pair<int,int> P[100000];
  for(int i=0;i<n;i++){
    P[i]=make_pair(A[i],B[i]);
    if((i!=0&&A[i]==0)||(i!=1&&B[i]==0)||A[i]+B[i]<L||A[i]-B[i]>L||B[i]-A[i]>L){
      puts("-1");
      return 0;
    }
  }
  sort(P,P+n);
  static bool X[100000];
  static int Y[100010]={0};
  X[0]=X[1]=1;
  for(int i=0;i<n;i++){
    if(A[i]+B[i]==L){
      X[i]=1;
      Y[A[i]]++;
    }
    else{
      int k=lower_bound(P,P+n,make_pair(A[i]-1,B[i]-1))-P;
      if(k<n&&P[k]==make_pair(A[i]-1,B[i]-1)){
	//printf("%d\n",i);
	X[i]=1;
	C++;
      }
    }
  }
  //printf("%d %d %d %d\n",C,Y[0],Y[1],Y[2]);
  for(int k=0;k<=L;k++){
    if(!Y[k]){
      puts("-1");
      return 0;
    }
  }
  for(int k=0;k<L;k++){
    C+=max(Y[k],Y[k+1]);
  }
  for(int i=0;i<n;i++){
    if(X[i]){
      continue;
    }
    else{
      int a1=A[i]-1,b1=min(A[i]-1+L,B[i]+1);
      int a2=min(B[i]-1+L,A[i]+1),b2=B[i]-1;
      int k1=lower_bound(P,P+n,make_pair(a1,b1))-P;
      int k2=lower_bound(P,P+n,make_pair(a2,b2))-P;
      if(k1<n&&P[k1]==make_pair(a1,b1)&&k2<n&&P[k2]==make_pair(a2,b2)){
	if(X[k1]){
	  C++;
	}
	C++;
      }
      else{
	puts("-1");
	return 0;
      }
    }
  }
  printf("%d\n",C);
  return 0;
}

#include <bits/stdc++.h>

using namespace std;

int N;
vector<int> A[100000];

void fail()
{
    printf("-1\n");
    exit(0);
}

int main()
{
    scanf("%d", &N);
    int maxb=0;
    int tmp=0;
    for(int i=0; i<N; i++)
    {
        int a, b;
        scanf("%d%d", &a, &b);
        if(i==0 && a!=0)
            fail();
        if(i==1 && b!=0)
            fail();
        if(i==0)
            tmp=b;
        if(i==1 && tmp!=a)
            fail();
        if(b==0 && i!=1)
            fail();
        A[a].push_back(b);
        maxb=max(maxb, b);
    }
    if(A[0].size()!=1)
        fail();
    for(int i=0; i<N; i++)
        sort(A[i].begin(), A[i].end());
    int ans=N-1;
    int maxi=0;
    for(int i=1; i<N; i++) if(!A[i].empty())
        maxi=i;
    for(int i=1; i<=maxi; i++)
    {
        if(A[i].empty())
            fail();
        for(auto& y: A[i])
        {
            int l=lower_bound(A[i-1].begin(), A[i-1].end(), y-1)-A[i-1].begin();
            int r=upper_bound(A[i-1].begin(), A[i-1].end(), y+1)-A[i-1].begin();
            if(l==r)
                fail();
            bool okl=y==0;
            bool okr=y==maxb;
            if(A[i-1][l]==y-1)
                okl=true;
            if(A[i-1][r-1]==y+1)
                okr=true;
            if((y!=0 && A[i-1][l]==y) && (y!=maxb && A[i-1][r-1]==y))
                ans++;
            if(i+1<=maxi)
            {
                l=lower_bound(A[i+1].begin(), A[i+1].end(), y-1)-A[i+1].begin();
                r=upper_bound(A[i+1].begin(), A[i+1].end(), y+1)-A[i+1].begin();
                if(l==r)
                    fail();
                if(A[i+1][l]==y-1)
                    okl=true;
                if(A[i+1][r-1]==y+1)
                    okr=true;
                if((y!=0 && A[i+1][l]==y) && (y!=maxb && A[i+1][r-1]==y))
                    ans++;
            }
            if(!okl || !okr)
            {
                l=lower_bound(A[i].begin(), A[i].end(), y-1)-A[i].begin();
                r=upper_bound(A[i].begin(), A[i].end(), y+1)-A[i].begin();
                if(l==r)
                    fail();
                if(!okl && A[i][l]==y-1)
                    okl=true, ans++;
                if(!okr && A[i][r-1]==y+1)
                    okr=true, ans++;
            }
            if(!okl || !okr)
                fail();
        }
    }
    printf("%d\n", ans);
    return 0;
}

