    for(int i = 0; i < N; i++) {
        scanf("%d %d", &A[i], &B[i]);

        Va[ A[i] ].push_back(i);
        Vb[ B[i] ].push_back(i);
    }

    if(A[0] || B[1]) {
        printf("-1");
        return 0;
    }
    if(Va[0].size() > 1 || Vb[0].size() > 1) {
        printf("-1");
        return 0;
    }

    multiset<int> pre;
    pre.insert(B[0]);

    for(int i = 1; i < N; i++) {
        multiset<int> cur;

        for(int j = 0; j < Va[i].size(); j++) {
            int x = Va[i][j];

            auto it = pre.lower_bound(B[x] - 1);
            if(it == pre.end() || B[x] + 1 < *it) {
                printf("-1");
                return 0;
            }

            cur.insert(B[x]);
        }
        pre = cur;
    }

    int ans = N - 1;

    pre.clear();
    pre.insert(A[1]);

    multiset<int> alive = pre;
    for(int i = 1; i < N; i++) {
        multiset<int> cur;
        multiset<int> tmp;

        for(int j = 0; j < Vb[i].size(); j++) {
            int x = Vb[i][j];

            auto it = pre.lower_bound(A[x] - 1);
            if(it == pre.end() || A[x] + 1 < *it) {
                printf("-1");
                return 0;
            }
            cur.insert(A[x]);
        }

        vector<int> chk(Vb[i].size(), 0);

        for(int j = 0; j < Vb[i].size(); j++) if(!chk[j]) {
            int x = Vb[i][j];

            if(alive.find(A[x] + 1) != alive.end()) {
                chk[j] = 1;
                alive.erase(alive.find(A[x] + 1));
                tmp.insert(A[x]);
            }
        }
        for(int j = 0; j < Vb[i].size(); j++) if(!chk[j]) {
            int x = Vb[i][j];

            if(pre.find(A[x] - 1) != pre.end()) {
