void tri(vpi v) {
    map<pi,int> m; trav(t,v) m[t] ++;
    auto V = vector<pair<pi,int>>(all(m));
    // ps(V);
    cur = 0;
    F0R(i,sz(V)) {
        if (i == 0) cur = V[i].s;
        elim(con({V[i].f.f-1,V[i].f.s-1}),V[i].s);
        if (i+1 < sz(V)) elim(1,V[i+1].s);
    }
    // ps("HA",ans);
}

