        int A,B; re(A,B);
        if (i == 0) as(A == 0);
        else as(A != 0);
        if (i == 1) as(B == 0);
        else as(B != 0);
        v.pb({A,B});
        S.insert({A,B});
    }
    trav(t,S) {
        if (t.s) {
            bool ok = 0;
            FOR(i,-1,2) ok |= S.count({t.f+i,t.s-1});
            as(ok);
        }
        if (t.f) {
            bool ok = 0;
            FOR(i,-1,2) ok |= S.count({t.f-1,t.s+i});
            as(ok);
        }
    }
    sort(all(v),[](pi a, pi b) {
        if (a.f+a.s != b.f+b.s) return a.f+a.s < b.f+b.s;
        return a.f < b.f;
    });
    for (int i = 0; i < sz(v);) {
        vpi tmp = {v[i]};
        while (i+1 < sz(v) && nex(v[i],v[i+1])) tmp.pb(v[++i]);
        i ++;
        tri(tmp);
    }
    ps(ans);
}

