				ret--;
				C[m.second[i]]=D[M[a][i]]=0;
			}
		}
	}
	FOR(i,N) if(C[i]+D[i]==2 && M.count({A[i]-1,B[i]-1})) ret--;
	cout<<ret<<endl;
}


