void NO(){puts("-1");exit(0);}
int main(){
    DRI(N);
    REP(i,N)RII(A[i+1],B[i+1]);
    if(A[1]||B[2]||B[1]!=A[2]||A[2]==0)NO();
    int dd=A[2];
    int an=dd;
    int one=0;
    int oneone=0;
    REPP(i,3,N+1){
        if(!A[i]||!B[i])NO();
        if(A[i]+B[i]==dd){
            cnt[0][A[i]]++;
            if(cnt[0][A[i]]>1)an+=2;
        }
        else if(A[i]+B[i]==dd+1){
            cnt[1][A[i]]++;
            if(cnt[1][A[i]]>1)an+=2;
            one=1;
        }
        else if(A[i]+B[i]<dd)NO();
        else{
            oneone=1;
            an++;
            int p=A[i]+dd-B[i];
            if(p%2){
                int p2=A[i]+dd+1-B[i];
                if(p2/2<=0||p2/2>dd)NO();
                d2[p2/2].PB(A[i]-p2/2);
            }
            else{
                if(p/2<0||p/2>dd)NO();
                d[p/2].PB(A[i]-p/2);
            }
        }
    }
    REPP(i,1,dd){
        if(!cnt[0][i])NO();
    }
    if(!one&&oneone)NO();
    if(one){
        an+=dd+1;
        REPP(i,1,dd+1){
            if(!cnt[1][i])NO();
        }
        REPP(i,1,dd+1){
            int lt=0;
            sort(ALL(d2[i]));
            REP(j,SZ(d2[i])){
                if(d2[i][j]!=lt&&d2[i][j]!=lt+1)NO();
                lt=d2[i][j];
            }
        }
    }
    REP(i,dd+1){
        int lt=0;
        sort(ALL(d[i]));
        REP(j,SZ(d[i])){
            if(d[i][j]!=lt&&d[i][j]!=lt+1)NO();
            lt=d[i][j];
        }
    }
    printf("%d\n",an);
    return 0;
}

