  rep (i, N) hige[i] = cnt.count(mp(A[i] - 1, B[i] - 1));

  map<int, map<int, int>> taba;
  rep (i, N) {
    if (hige[i]) continue;
    taba[A[i] + B[i]][A[i]] += 1;
  }

  for (int d = 0; d < D; ++d) {
    if (taba.count(d)) dmp();
  }

  int ans = N - 2;  //count(hige, hige + N, true);

  for (auto &i : taba) {
    map<int, int> &m = i.second;
    int d = i.first;

    for (const auto &j : m) {
      int a = j.first, b = d - a, c = j.second;

      int prv_c = cnt[mp(a - 1, b + 1)];
      int nxt_c = cnt[mp(a + 1, b - 1)];
      // printf("%d %d: %d %d %d\n", a, b, prv_c, c, nxt_c);

      int bgn = max(0, c - prv_c);
      int end = max(0, c - nxt_c);

      if (bgn > 0) {
        // printf(" %d\n", bgn);
        ans += bgn;
        if (a > 0 && (prv_c == 0 && cnt[mp(a - 1, b)] == 0)) {
          //printf("%d %d\n", a, b);
          dmp();
        }
      }
      if (end > 0) {
        if (b > 0 && (nxt_c == 0 && cnt[mp(a, b - 1)] == 0)) {
          //          printf("%d %d\n", a, b);
          dmp();
        }
      }
    }
  }

  printf("%d\n", ans);

  return 0;
}

