int N;
int A[100010], B[100010];

void dmp() {
  puts("-1");
  exit(0);
}

int main() {
  scanf("%d", &N);
  rep (i, N) {
    scanf("%d%d", &A[i], &B[i]);

    if ((A[i] == 0) != (i == 0)) dmp();
    if ((B[i] == 0) != (i == 1)) dmp();
  }

  int D = B[0];
  if (A[1] != D) dmp();

  map<pair<int, int>, int> cnt;
  rep (i, N) cnt[mp(A[i], B[i])] += 1;

  map<int, map<int, int>> taba;
  rep (i, N) {
    if (i == 1) continue;
    taba[A[i] + B[i]][A[i]] += 1;
  }

  for (int d = 0; d < D; ++d) {
    if (taba.count(d)) dmp();
  }

  int ans = 1;

  for (auto &i : taba) {
    map<int, int> &m = i.second;
    int d = i.first;

    int request = 0;  // request from left
    for (const auto &j : m) {
      int a = j.first, b = d - a, c = j.second;
      if (a > 0 && cnt[mp(a - 1, b - 1)] + cnt[mp(a - 1, b)] + cnt[mp(a - 1, b + 1)] == 0) {
        dmp();
      }

      if (a > 0) {
        ans += c;
        //printf("%d %d: +=%d\n", a, b, c);
      }

      int hige;
      if (cnt[mp(a - 1, b - 1)]) hige = max(0, c - request);
      else hige = 0;
      //printf("%d %d: hige=%d\n", a, b, hige);
      c -= hige;

      ans += max(0, request - c);

      if (cnt[(mp(a + 1, b - 1))] > 0) {
        request = c;
      } else {
        if (b > 0 && cnt[mp(a - 1, b - 1)] + cnt[mp(a, b - 1)] + cnt[mp(a + 1, b - 1)] == 0) {
          dmp();
        }
        //printf("%d %d: -=%d\n", a, b, c);
        ans += c;
        request = 0;
      }
    }
  }

  printf("%d\n", ans);

  return 0;
}

