    forn (i, n) {
        int x, y;
        cin >> x >> y;
        if (i == 0) {
            ass(x == 0);
            d = y;
        }
        if (i == 1)
            ass(y == 0 && d == x);
        ass(x + y >= d);
        ass(x >= 0 && y >= 0);
        if (i != 0)
            ass(x != 0);
        if (i != 1)
            ass(y != 0);
        a[pii(x, y)]++;
    }
    for (auto p: a) {
        auto c = p.first;
        if (c.second != 0)
            ass(a.count(c + pii(0, -1)) || a.count(c + pii(-1, -1)) || a.count(c + pii(1, -1)));
        if (c.first != 0)
            ass(a.count(c + pii(-1, 0)) || a.count(c + pii(-1, -1)) || a.count(c + pii(-1, 1)));
    }
    //cerr << "YES\n";
    vector<pair<pii, int>> v;
    for (auto A: a)
        v.push_back(A);
    sort(v.begin(), v.end(), [](pair<pii, int> a, pair<pii, int> b) {
                int sa = a.first.first + a.first.second;
                int sb = b.first.first + b.first.second;
                if (sa != sb)
                    return sa < sb;
                return a.first < b.first;
            });
    int res = 2 * n - 2;

    int carry = 0;
    forn (ii, sz(v)) {
        auto c = v[ii].first;
        int cnt = v[ii].second;
        carry = min(carry, cnt);
        if (a.count(c + pii{-1, -1})) {
            int k = cnt - carry;
            res -= k;
            cnt -= k;
        }
        res -= carry;
        if (ii + 1 < sz(v) && v[ii + 1].first == nx(c))
            carry = cnt;
        else
            carry = 0;
    }
    cout << res << '\n';
}

