    }

    for (auto item : cnt) {
        if (item.first == 0 || item.first == d) {
            if (item.second != 1) {
                cout << "-1\n";
                return;
            }
        }
        ans += item.second - 1;
    }

    if (cnt.size() != dist + 1) {
        cout << "-1\n";
        return;
    }

    cout << ans << "\n";

}

