	}

	FOR(i,N) {
		if(i==0) {
			if(A[i]) return _P("-1\n");
		}
		else {
			C[i]=1;
			if(M.count({A[i]-1,B[i]-1})+M.count({A[i]-1,B[i]})+M.count({A[i]-1,B[i]+1})==0) return _P("-1\n");
		}
		if(i==1) {
