    DRI(N);
    dij.init(N);
    REP(i,N){
        RII(A[i+1],B[i+1]);
        H[MP(A[i+1],B[i+1])]=i+1;
    }
    if(A[1]||B[2]||B[1]!=A[2]||A[2]==0)NO();
    int dd=A[2];
    if(dd==1)dij.add_edge(1,2,1);
    REPP(i,3,N+1){
        if(A[i]+B[i]<dd)NO();
        if(H.count(MP(A[i]-1,B[i]-1))){
            dij.add_edge(H[MP(A[i]-1,B[i]-1)],i);
        }
        else{
            int id1=-1,id2=-1;
            if(H.count(MP(A[i]-1,min(B[i]+1,A[i]-1+dd)))){
                id1=H[MP(A[i]-1,min(B[i]+1,A[i]-1+dd))];
            }
            if(H.count(MP(min(A[i]+1,B[i]-1+dd),B[i]-1))){
                id2=H[MP(min(A[i]+1,B[i]-1+dd),B[i]-1)];
            }
            if(id1==-1||id2==-1)NO();
            dij.add_edge(id1,i);
            dij.add_edge(id2,i);
        }
    }
    dij.dis(1,-1);
    REPP(i,1,N+1){
        if(A[i]!=dij.mi[i])NO();
    }
    dij.dis(2,-1);
    REPP(i,1,N+1){
        if(B[i]!=dij.mi[i])NO();
    }
    printf("%d\n",SZ(pp));
    return 0;
}

