int main() {
	int N;
	scanf("%d", &N);
	vector<pair<int, int> > A;
	for(int i=0; i<N; ++i) {
		int a, b;
		scanf("%d%d", &a, &b);
		A.emplace_back(a, b);
	}
	if(A[0].first!=0 || A[1].second!=0 || A[1].first!=A[0].second) {
		printf("-1\n");
		return 0;
	}
	int ab = A[0].second;
	int sum = ab;
	{
		vector<int> vec(ab+1);
		for(auto & p : A) {
			if(p.first+p.second==ab) {
				++vec[p.first];
			}
		}
		if(vec[0]!=1 || vec[ab]!=1) {
			printf("-1\n");
			return 0;
		}
		for(int i : vec) {
			if(i==0) {
				printf("-1\n");
				return 0;
			}
			if(2<=i) {
				sum += (i-1)*2;
			}
		}
	}
	{
		vector<pair<int, int> > vec;
		for(auto & p : A) {
			if(p.first+p.second!=ab) {
				int aa = p.first+p.second - ab;
				int aaHalf = (aa+1)>>1;
				int aaa = p.first-aaHalf;
				if(aa<=0) {
					printf("-1\n");
					return 0;
				}
				if(aa&1) {
					vec.emplace_back(p.first-aaHalf, aaHalf);
					++sum;
				}
				vec.emplace_back(p.first-(aa>>1), aaHalf);
				++sum;
			}
		}
		sort(vec.begin(), vec.end());
		int beforeF = -1;
		int beforeS = 0;
		for(auto & p : vec) {
			if(p.first!=beforeF) {
				beforeF = p.first;
				beforeS = 0;
			}
			if(p.second!=beforeS && p.second!=beforeS+1) {
				printf("-1\n");
				return 0;
			}
			beforeS = p.second;
		}
	}
	printf("%d\n", sum);

	return 0;
}

