int N;
int A[101010],B[101010];
int L;
int did[101010];
int ok[101010];

set<pair<int,int>> S;

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	FOR(i,N) cin>>A[i]>>B[i];

	if(A[0]!=0) return _P("-1\n");
	if(B[1]!=0) return _P("-1\n");
	if(A[1]!=B[0]) return _P("-1\n");
	L=A[1];
	ok[0]=ok[L]=1;
	did[0]=did[1]=1;

	S.insert({0,L});
	S.insert({L,0});
	vector<pair<int,int>> V;
	int ret=1;
	for(i=2;i<N;i++) {
		if(A[i]==0 || B[i]==0) return _P("-1\n");
		if(A[i]+B[i]<L) return _P("-1\n");
		if(A[i]+B[i]==L) {
			did[i]=1;
			ok[A[i]]=1;
			ret++;
			S.insert({A[i],B[i]});
		}
		else {
			V.push_back({A[i]+B[i],i});
		}
	}
	FOR(i,L+1) if(ok[i]==0) return _P("-1\n");
	sort(ALL(V));
	FORR(r,V) {
		x = r.second;
		if((A[x]+B[x]-L)%2==0) {
			if(S.count({A[x]-1,B[x]-1})==0) return _P("-1\n");
			ret++;

		}
		else {
			if(S.count({A[x]-1,B[x]})==0 || S.count({A[x],B[x]-1})==0) return _P("-1\n");
			ret+=2;
		}
		S.insert({A[x],B[x]});
	}
	_P("%d\n",ret);


}


