#include <cstdio>
#include <vector>
#include <map>

using namespace std;

map <pair<int, int>, int> mp[200000];

bool check(pair<int, int> p, int da, int db) {
    int a = p.first + da;
    int b = p.second + db;

    return mp[a + b].count(make_pair(a, b));
}

bool before(pair<int, int> p) {
    return check(p, -1, -1) || check(p, -1, 0);
}

bool after(pair<int, int> p) {
    return check(p, -1, -1) || check(p, 0, -1);
}

int calc(vector <pair<bool, int> >& v) {
    int ans = 0, c = 0, i;

    for (i = 0; i < v.size(); i++) {
        ans += v[i].second;

        if (v[i].first) {
            c = min(c, v[i].second);
        } else {
            if (v[i].second > c) ans += v[i].second - c;
            c = v[i].second;
        }
    }

    return ans;
}

int solve() {
    int n, d, ans = 0, i;
    map <pair<int, int>, int>::iterator it, it2;

    scanf("%d", &n);

    for (i = 0; i < n; i++) {
        int a, b;

        scanf("%d %d", &a, &b);

        if (i == 0) {
            if (a != 0 || b == 0) return -1;

            d = b;
        } else if (i == 1) {
            if (a != d || b != 0) return -1;
        } else {
            if (a == 0 || b == 0 || a + b < d) return -1;
        }

        mp[a + b][make_pair(a, b)]++;
    }

    for (it = mp[d].begin(); it != mp[d].end(); it++) {
        if (it == mp[d].begin()) continue;

        it2 = it;
        it2--;

        if (it2->first.first + 1 != it->first.first) return -1;

        ans += max(it->second, it2->second);
    }

    for (i = d + 1; i < n * 2; i++) {
        vector <pair<bool, int> > v;

        if (mp[i].size() == 0) continue;

        for (it = mp[i].begin(); it != mp[i].end(); it++) {
            if (it == mp[i].begin()) {
                if (!before(it->first)) return -1;

                v.push_back(make_pair(check(it->first, -1, -1), it->second));
            } else {
                it2 = it;
                it2--;

                if (it2->first.first + 1 != it->first.first) {
                    if (!after(it2->first) || !before(it->first)) return -1;

                    ans += calc(v);
                    v.clear();
                }

                v.push_back(make_pair(check(it->first, -1, -1), it->second));
            }
        }

        it--;
        if (!after(it->first)) return -1;

        ans += calc(v);
    }

    return ans;
}

int main() {
    printf("%d\n", solve());

    return 0;
}

#include <cstdio>
#include <map>
#include <algorithm>

using namespace std;

map <pair<int, int>, int> mp;
map <pair<pair<int, int>, pair<int, int> >, int> mp2;

bool check(int a, int b, int da, int db) {
    return mp.count(make_pair(a + da, b + db));
}

void add(int a, int b, int da, int db, int c) {
    pair<int, int> p1 = make_pair(a, b);
    pair<int, int> p2 = make_pair(a + da, b + db);

    if (p1 > p2) swap(p1, p2);

    mp2[make_pair(p1, p2)] = max(mp2[make_pair(p1, p2)], c);
}

int solve() {
    int n, d, ans = 0, i;
    map <pair<int, int>, int>::iterator it;
    map <pair<pair<int, int>, pair<int, int> >, int>::iterator it2;

    scanf("%d", &n);

    for (i = 0; i < n; i++) {
        int a, b;

        scanf("%d %d", &a, &b);

        if (i == 0) {
            if (a != 0 || b == 0) return -1;

            d = b;
        } else if (i == 1) {
            if (a != d || b != 0) return -1;
        } else {
            if (a == 0 || b == 0 || a + b < d) return -1;
        }

        mp[make_pair(a, b)]++;
    }

    for (it = mp.begin(); it != mp.end(); it++) {
        int a = it->first.first;
        int b = it->first.second;
        int c = it->second;

        if (a != 0) {
            if (check(a, b, -1, 1)) {
                add(a, b, -1, 1, c);
            } else if (check(a, b, -1, -1)) {
                add(a, b, -1, -1, c);
            } else if (check(a, b, -1, 0)) {
                add(a, b, -1, 0, c);
            } else {
                return -1;
            }
        }

        if (b != 0) {
            if (check(a, b, 1, -1)) {
                add(a, b, 1, -1, c);
            } else if (check(a, b, -1, -1)) {
                add(a, b, -1, -1, c);
            } else if (check(a, b, 0, -1)) {
                add(a, b, 0, -1, c);
            } else {
                return -1;
            }
        }
    }

    for (it2 = mp2.begin(); it2 != mp2.end(); it2++) ans += it2->second;

    return ans;
}

int main() {
    printf("%d\n", solve());

    return 0;
}

