set<pii> S;

void ch(bool x) {
	if (!x) no();
}

int cc(pii p1, pii p2) {
	if (p1.first + p1.second != p2.first + p2.second) return p1.first + p1.second < p2.first + p2.second;
	return p1 < p2;
}

void solve() {
	ch(a[1] == 0);
	ch(b[2] == 0);
	ch(a[2] == b[1]);

	int d = a[2];
	ch(d > 0);

	vector<pii> v;

	ans = n - 1;

	CL(0, u);
	m = 0;

	for (int i = 1; i <= n; i++) {
		if (i > 2) ch(a[i] > 0 && b[i] > 0);
		ch(a[i] + b[i] >= d);
		if (a[i] + b[i] == d && !u[a[i]]) {
			S.insert(pii(a[i], b[i]));
			u[a[i]] = 1; m++;
		}
		else {
			v.push_back(pii(a[i], b[i]));
		}
	}
	ch(m == d + 1);
	sort(v.begin(), v.end(), cc);

	set<pii> S2;

	for (pii p : v) {
		if (S.count(pii(p.first - 1, p.second - 1))) { S.insert(p); continue; }
		if (S.count(pii(p.first, p.second))) { 
			if (S.count(p) && S2.count(pii(p.first - 1, p.second + 1))) continue;
			S2.insert(p);  S.insert(p);  ans++; continue;
		}
		//if (S.count(pii(p.first, p.second - 1))) { ans++; S.insert(p); S2.insert(p);  continue; }
		//if (S.count(pii(p.first - 1, p.second))) { ans++; S.insert(p); S2.insert(p);  continue; }
		if (S.count(pii(p.first, p.second - 1)) && S.count(pii(p.first - 1, p.second))) { ans++; S2.insert(p);  S.insert(p); continue; }
		no();
	}
}

