		cout << ans << endl;
		exit(0);
	}

	F0(it, 10000000) {
		int edges = 0;
		n = 5 + rand()%10;
		vector<int> p(n + 1);
		vector< vector<int> > d(n + 1, vector<int>(n + 1, inf));
		F1(i, n) d[i][i] = 0;
		F1(i, n) p[i] = i;
		int cnt = n - 1;
		while (cnt > 0) {
			int i = rand() % n + 1;
			int j = rand() % n + 1;
			if (i == j) continue;
			d[i][j] = d[j][i] = 1;
			edges++;
			while (i != p[i]) i = p[i];
			while (j != p[j]) j = p[j];
			if (i != j) {
				p[i] = j;
				cnt--;
			}
		}
		F1(k, n)F1(i, n) F1(j, n) d[i][j] = min(d[i][j], d[i][k] + d[k][j]);
		F1(i, n) {
			if (d[1][i] >= inf || d[2][i] >= inf) throw 12;
			a[i] = d[1][i];
			b[i] = d[2][i];
		}

		solve();
		if (edges < ans) {
			cout << endl;
			cout << edges << " " << ans << endl;
			F1(i, n) cout << a[i] << " " << b[i] << endl;
			throw 12;
		}
	}

	return 0;
}

