		int l = 0;
		for(int j = 0; j < xval.size(); j++){
			int numl = freq[j];
			int numd = freq[j];
			numl -= l;
			if(numl < 0) numl = 0;
			int xc = xval[j];
			int yc = i-xval[j];
			if(xc == 0) numl = 0;
			if(yc == 0) numd = 0;
			if(d[xc-1][yc-1]){
				int r = min(numl,numd);
				numl -= r;
				numd -= r;
				ans += r;
			}
			ans += numl;
			l = numd;
			ans += numd;
			if(j + 1 == xval.size() || xval[j+1] != xval[j] + 1) l = 0;	
		}
	}
	cout << ans << endl;

}

