int read() {
  if (scanf("%d", &n) < 1) {
    return 0;
  }
  for (int i = 0; i < n; ++i) {
    scanf("%d%d", &ds[0][i], &ds[1][i]);
  }
  return 1;
}

int res;

void solve() {
  if (ds[0][0] || ds[1][1]) {
    printf("-1\n");
    return;
  }
  int d = ds[0][1];
  if (ds[1][0] != d) {
    printf("-1\n");
    return;
  }
  vector<int> big(d + 1, 0);

  vector<int> vs[2][d + 1];
  for (int v = 0; v < n; ++v) {
    int left = ds[0][v] + ds[1][v] - d;
    if (left < 0 || abs(ds[0][v] - ds[1][v]) > d) {
      printf("-1\n");
      return;
    }
    if (left > 0) {
      vs[left & 1][(ds[0][v] - ds[1][v] + d) / 2].pb((left + 1) / 2);
    } else {
      big[ds[0][v]] += 1;
    }
  }
  res = 0;
  for (int odd = 0; odd < 2; ++odd) {
    vector<int> cool;
    for (int i = 0; i <= d - odd; ++i) {
      auto &cur = vs[odd][i];
      sort(cur.begin(), cur.end());
      int last = 0;
      for (int i = 0; i < sz(cur);) {
        int i0 = i;
        while (i < sz(cur) && cur[i] == cur[i0]) {
          ++i;
        }
        //eprintf("%dx  %d\n", i - i0, cur[i0]);
        if (cur[i0] != last + 1) {
          printf("-1\n");
          return;
        }
        res += i - i0;
        if (odd && cur[i0] == 1) {
          cool.pb(i - i0);
        }
        ++last;
      }
      if (sz(cool) == i) {
        cool.pb(0);
      }
    }
    if (odd) {
      cool.pb(0);
      for (int i = 0; i < sz(cool) - 1; ++i) {
        int cur = cool[i + 1] - cool[i];
        if (cur > 0) {
          res += cur;
        }
      }
    }
  }
  if (big[0] != 1 || big[d] != 1) {
    printf("-1\n");
    return;
  }
  for (int i = 1; i < d; ++i) {
    if (big[i] == 0) {
      printf("-1\n");
      return;
    }
  }
  for (int i = 0; i < d; ++i) {
    res += max(big[i], big[i + 1]);
  }
  printf("%d\n", res);
}

void gen() {
  int m;
  while (1) {
    beg:;
  n = rnd(10) + 2;
  m = 0;
  vector<vector<int> > es(n);
  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      if (rnd(4) == 0) {
        es[i].pb(j), es[j].pb(i);
        eprintf("%d %d\n", i, j);
        ++m;
      }
    }
  }

  for (int i = 0; i < 2; ++i) {
    auto &dist = ds[i];
    for (int v = 0; v < n; ++v) {
      dist[v] = inf;
    }
    vector<int> st;
    st.pb(i);
    dist[i] = 0;
    for (int l = 0; l < sz(st); ++l) {
      int v = st[l];
      for (int u : es[v]) {
        if (dist[u] > dist[v] + 1) {
          st.pb(u);
          dist[u] = dist[v] + 1;
        }
      }
    }
    if (sz(st) < n) {
      goto beg;
    }
  }
  break;
  }
  eprintf("n = %d\n", n);
  solve();
  if (res == -1 || res > m) {
    assert(0);
  }
}
