//  -  - 
int
fTreeCmp(
	Tree *pzpTree				// <I> 
	, int piLen1				// <I> 
	, int piLen2				// <I> 
)
{
	// 
	Vtx *lzpVtx = pzpTree->mzpVtx;

	// 
	if (piLen1 < lzpVtx->miLen1) {			// 
		return -1;
	}
	else if (piLen1 > lzpVtx->miLen1) {		// 
		return 1;
	}
	if (piLen2 < lzpVtx->miLen2) {			// 
		return -1;
	}
	else if (piLen2 > lzpVtx->miLen2) {		// 
		return 1;
	}

	return 0;
}

//  - 
Tree *
fTreeSrh(
	int piLen1					// <I> 
	, int piLen2				// <I> 
)
{
	// 
	Tree *lzpNow = szpTop;

	// 
	while (1) {

		// 
		if (lzpNow == NULL) {
			return NULL;
		}

		// 
		int liRet = fTreeCmp(lzpNow, piLen1, piLen2);
		if (liRet == 0) {								// 
			return lzpNow;
		}

		// 
		if (liRet < 0) {								// 
			lzpNow = lzpNow->mzp1Child[D_TREE_LEFT];
		}
		else {											// 
			lzpNow = lzpNow->mzp1Child[D_TREE_RIGHT];
		}
	}

	return NULL;
}

//  - 
int
fTreeGetHeight(
	Tree *pzpTree				// <I> 
)
{
	// 
	if (pzpTree == NULL) {
		return 0;
	}

	if (pzpTree->mi1Height[D_TREE_LEFT] >= pzpTree->mi1Height[D_TREE_RIGHT]) {
		return pzpTree->mi1Height[D_TREE_LEFT] + 1;
	}
	else {
		return pzpTree->mi1Height[D_TREE_RIGHT] + 1;
	}
}

//  - ([][])
int
fTreeRttR(
	Tree **pzppTree				// <I> 
)
{
	// []
	Tree *lzpChild = (*pzppTree)->mzp1Child[D_TREE_LEFT];

	// 
	(*pzppTree)->mzp1Child[D_TREE_LEFT] = lzpChild->mzp1Child[D_TREE_RIGHT];	//  = []
	(*pzppTree)->mi1Height[D_TREE_LEFT] = lzpChild->mi1Height[D_TREE_RIGHT];	// () = []()
	lzpChild->mzp1Child[D_TREE_RIGHT] = *pzppTree;								// [] = 
	lzpChild->mi1Height[D_TREE_RIGHT] = fTreeGetHeight(*pzppTree);				// []() - 
	*pzppTree = lzpChild;														//  = []

	return 0;
}

//  - ([][])
int
fTreeRttL(
	Tree **pzppTree				// <I> 
)
{
	// []
	Tree *lzpChild = (*pzppTree)->mzp1Child[D_TREE_RIGHT];

	// 
	(*pzppTree)->mzp1Child[D_TREE_RIGHT] = lzpChild->mzp1Child[D_TREE_LEFT];	//  = []
	(*pzppTree)->mi1Height[D_TREE_RIGHT] = lzpChild->mi1Height[D_TREE_LEFT];	// () = []()
	lzpChild->mzp1Child[D_TREE_LEFT] = *pzppTree;								// [] = 
	lzpChild->mi1Height[D_TREE_LEFT] = fTreeGetHeight(*pzppTree);				// []() - 
	*pzppTree = lzpChild;														//  = []

	return 0;
}

//  - 
// [2] [1]
int
fTreeComAddDel(
	Tree **pzppNow				// <I> 
	, int piWay					// <I> 
)
{
	// 
	int liNew = fTreeGetHeight((*pzppNow)->mzp1Child[piWay]);
	if ((*pzppNow)->mi1Height[piWay] == liNew) {												// 
		return 1;
	}
	(*pzppNow)->mi1Height[piWay] = liNew;														// 

	// 
	if ((*pzppNow)->mi1Height[D_TREE_LEFT] - (*pzppNow)->mi1Height[D_TREE_RIGHT] > 1) {			// 
		fTreeRttR(pzppNow);																			// 
	}
	else if ((*pzppNow)->mi1Height[D_TREE_RIGHT] - (*pzppNow)->mi1Height[D_TREE_LEFT] > 1) {	// 
		fTreeRttL(pzppNow);																			// 
	}

	return 2;
}

//  - 
// [2] [1] [0]
int
fTreeAdd(
	Tree **pzppNow				// <I> 
	, Vtx *pzpVtx				// <I> 
)
{
	// 
	if (*pzppNow == NULL) {
		*pzppNow = fTreeMake(pzpVtx);
		return 2;
	}

	// 
	int liRet = fTreeCmp(*pzppNow, pzpVtx->miLen1, pzpVtx->miLen2);

	// 
	if (liRet == 0) {
		return 0;
	}

	// 
	int liWay;
	if (liRet < 0) {															// 
		liWay = D_TREE_LEFT;
	}
	else {																		// 
		liWay = D_TREE_RIGHT;
	}

	// 
	liRet = fTreeAdd(&((*pzppNow)->mzp1Child[liWay]), pzpVtx);
	if (liRet < 2) {															// 
		return liRet;
	}

	// 
	return fTreeComAddDel(pzppNow, liWay);
}

