	// 
	int liVNo = 0;
	sz1Vtx[siVCnt].miSum = INT_MAX;
	int liECnt = -2;
	for (i = siVLen; i <= siVCnt * 2; i++) {

		//  - 
		if (sz1Vtx[liVNo].miSum != i) {
			continue;
		}
		int liSNo = liVNo;
		int liENo;
		while (1) {
			liENo = liVNo;
			liVNo++;
			if (sz1Vtx[liVNo].miSum != i) {
				break;
			}
		}

		// 
		char mcDown = D_ON;
		int liACntLine = 0;
		int liACntAll = 0;
		for (j = liSNo; j <= liENo; j++) {

			//  - 
			liRet = fTreeAdd(&szpTop, &sz1Vtx[j]);
			if (liRet > 0) {
				liACntLine++;
			}
			liACntAll++;

			// 
			Tree *lzpTree = fTreeSrh(sz1Vtx[j].miLen1 - 1, sz1Vtx[j].miLen2 - 1);
			if (lzpTree == NULL) {
				mcDown = D_OFF;
			}
		}

		//  - 
		if (mcDown == D_ON) {				
			liECnt += liACntAll;
		}
		else {

			// 
			if (i == siVLen) {
				if (liACntLine != i + 1) {
					return -1;
				}
			}
			else {
				if (liACntLine != i - 1) {
					return -1;
				}
			}

			liECnt += liACntAll + 1;
		}
	}

	return liECnt;
}

