#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct ll{
  int data;
  int num;
  struct ll *next;
} DL;

DL *createList(int data, int n){
  DL *new;
  new = (DL *)malloc(sizeof(DL));
  int i;
  new->data = data;
  new->num = n;

  return new;
}

void addList(DL **head, DL *add){
  add->next = *head;
  *head = add;
  return;
}

void deleteList(DL **pt){
  DL *del = *pt;
  *pt = del->next;

  free(del);
  return;
}

void printList(DL *head){
  puts("LIST---------------");
  while(head != NULL){
    printf("[%d]%d ", head->num, head->data);
    head = head->next;
  }
  putchar('\n');
  puts("---------------LIST");
  return;
}

