  int data2[100000]={0};

  DL *head1=NULL;
  DL *head2=NULL;
  DL **search = NULL;
  scanf("%d", &N);

  // 
  for(i=0; i<N; i++){
    scanf("%d%d", &a, &b);
    find1=find2=0;
    a++;b++;
    if(a==1){
      data1[i]=a;
      find1++;
    }
    if(b==1){
      data2[i]=b;
      find2++;
    }
    for(j=0; j<N; j++){
      if(data1[j] == a-1){
        data1[i]=a;
        find1++;
      }
      if(data2[j] == b-1){
        data2[i]=b;
        find2++;
      }
      if(find1 && find2) break;
    }
    if(j==N){
      if(!find1){
        DL *add = createList(a, i);
        addList(&head1, add);
      }
      if(!find2){
        DL *add = createList(b, i);
        addList(&head2, add);
      }
    }
  }


  // 
