  find1 = 1;
  while(find1 && head1 != NULL){
    search = &head1;
    find1 = 0;
    while(*search != NULL){
      n = (*search)->num;
      a = (*search)->data;
      for(i=0; i<N; i++){
        if(data1[i] == a-1 && abs(data2[n] - data2[i]) == 1){
          find1++;
          break;
        }
      }
      if(find1){
        data1[n] = a;
        deleteList(search);
      }else{
        search = &(*search)->next;
      }
    }
  }

  if(head1 == NULL){
    find2 = 1;
    while(find2 && head2 != NULL){
      search = &head2;
      find2 = 0;
      while(*search != NULL){
        n = (*search)->num;
        b = (*search)->data;
        for(i=0; i<N; i++){
          if(data2[i] == b-1 && abs(data1[n] - data1[i]) == 1){
            find2++;
