signed main(){
  Int n,k;
  cin>>n>>k;
  k--;
  using D = long double;
  const D PI = acos(-1.0);
  vector<D> ss(n+1);
  for(Int i=0;i<=n;i++)
    ss[i]=sin((D)i/n*PI);

  auto check=
    [&](D x)->Int{
      auto calc=[&](Int a,Int b)->D{return ss[a+b]*ss[a]*ss[b];};
      Int cnt=0;
      // seisankaku (a = b = c)
      if(n%3==0){
        Int e=n/3;
        if(calc(e,e)<=x) cnt+=e;
      }
      // nitouhen (a = b)
      for(Int a=1;a*2<n;a++){
        if(a+a+a==n) continue;
        Int b=a;
        if(calc(a,b)<=x) cnt+=n; 
      }
      // sonota (a < b, a < c, b != c)
      for(Int a=1;a*3<n;a++){
        // a < b
        Int bl=a+1;
        // a < c = n - (a + b)
        Int br=n-2*a-1;
        if(bl>br) continue;

        // [bl, br]
        Int l=bl,r=br;
        while(l+1<r){
          Int m=(l+r)>>1;
          if(calc(a,m)>calc(a,m+1)) l=m;
          else r=m;
        }
        Int t=calc(a,l)<calc(a,r)?l:r;
        if(calc(a,t)>x) continue;        
        {
          bl--;
          Int bt=t;
          while(bl+1<bt){
            Int bm=(bl+bt)>>1;
            if(calc(a,bm)<=x) bt=bm;            
            else bl=bm;
          }
          bl=bt;
        }
        {
          br++;
          Int bt=t;
          while(bt+1<br){
            Int bm=(bt+br)>>1;
            if(calc(a,bm)<=x) bt=bm;            
            else br=bm;
          }
          br=bt;
        }        
        // cnt+=(br-bl+1)*n;
        for(Int b=bl;b<=br;b++){
          Int c=n-(a+b);
          if(a>=c) continue;
          if(b==c) continue;          
          if(calc(a,b)<=x) cnt+=n; 
        }        
      }
      return cnt>=k;
    };

  D L=0,R=1;
  for(Int k=0;k<50;k++){
    D M=(L+R)/2;
    if(check(M)) R=M;
    else L=M;
  }
  cout<<R*2<<endl;
  return 0;
}

#include <bits\stdc++.h>
#define area(x,y) 2*Sin[x]*Sin[y]*Sin[n-x-y]
#define db long double
#define m (l+r>>1)
#define pi (db) 3.14159265358979323846
#define N 300000
using namespace std;
int L[N],R[N],tmp[N],n;
long long k;
db Sin[N],ans;
int main() {
	cin>>n>>k;
	if (n%3==0&&k>1LL*n*(n-1)*(n-2)/6-n/3) {printf("1.29903810568\n");return 0;}
	for (int i=1;i<=n;i++) Sin[i]=sin(i*pi/n);
	for (int i=1;i<=n/3;i++) L[i]=i,R[i]=(n-i)/2;
	db ls=0,rs=1.39903810568;
	while (true) {
		db s=(ls+rs)/2;
		int p=0,q=0;ans=0;
		for (int i=1;i<=n/3;i++) {
			int l=L[i],r=R[i]+1;
			if (area(i,L[i])>s) break;
			while (r-l>1) if (area(i,m)<s) l=m;else r=m;
			tmp[i]=m;
			if (ans<area(i,m)-1e-7) q=0,ans=area(i,m);
			if (abs(ans-area(i,m)<1e-7)) {q+=n;if (m!=L[i]&&m*2!=n-i) q+=n;};
			p+=n*(m-i+1)*2-n;
			if ((n-i)%2==0&&m==R[i]) p-=n;
		}
		if (p<k) for (int i=1;i<=n;i++) L[i]=max(L[i],tmp[i]),ls=s;
		if (k<=p-q) for (int i=1;i<=n;i++) R[i]=min(R[i],tmp[i]+1),rs=s;
		if (k<=p&&p-q<k) break;
	}
	cout<<setprecision(20);
	cout<<ans;
}

