int main() {
	//printf("%.12lf\n",sqrt(pi));
	cin>>n>>k;
	if(n==3) {
		printf("%.10lf\n",3*sqrt(3)/4);
		return 0;
	}
	ll l=n/2;
	ll c=n/3;
	len[1]=2*sin(pi/n);
	long double l2=len[1]*len[1];
	a[1]=pi-2*pi/n;
	for(ll i=2;i<=l-1;i++) {
		a[i]=a[i-1]-pi/n;
	}
	for(ll i=2;i<=l;i++) {
		len[i]=sqrt(l2+len[i-1]*len[i-1]-2*len[1]*len[i-1]*cos(a[i-1]));
	}
	ll cnt,id=2;
	s[1].num=n;
	//printf("%.12lf %.12lf\n",len[1],len[2]);
	s[1].S=sqrt((len[1]+len[2]/2)*(len[2]*len[2]/4)*(len[1]-len[2]/2));
	//printf("%.12lf\n",s[1].S);
	for(ll i=1;i<=c;i++) {
		for(ll j=i;j<=(n-i)/2;j++) {
			ll k=n-i-j;
			if(i==1 && j==1) continue;
			ll tmp;
			if(i==j && j==k) {
				tmp=n/3;
			} else if(k==j || i==j || i==k) {
				tmp=n;
			} else {
				tmp=n*2;
			}
			s[id].num=tmp;
			long double half=(len[i]+len[j]+len[min(k,n-k)])/2;
			s[id].S=sqrt(half*(half-len[i])*(half-len[j])*(half-len[min(k,n-k)]));
			id++;
			//cnt+=tmp;
		}
	}
	id--;
	cnt=0;
	sort(s+1,s+id+1,cmp);
	/*
	for(ll i=1;i<=l;i++) {
		printf("%.8lf ",len[i]);
