	for(ll i=1;i<=id;i++) {
		printf("%d: %.8lf ",s[i].num,s[i].S);
	}
	cout<<endl<<id<<endl;
	*/
	for(ll i=1;i<=id;i++) {
		cnt+=s[i].num;
		//cout<<cnt<<endl;
		if(cnt>=k) {
			printf("%.10Lf",s[i].S);
			return 0;
		}
	}
	//printf("%d\n",cnt);
	//printf("%.12lf\n",sin(pi/n));
	return 0;
}

