ll N,K;
double T[202020];

double AA(int x,int y) {
	return T[x]+T[y-x]+T[N-y];
}

ll num(double A) {
	ll ret=0;
	for(int x=1;x<N;x++) {
		int y=x+(N-x)/2;
		int tx=x;
		if(AA(x,y)<=A) {
			ret += (N-(x+1));
			continue;
		}
		while(y-tx>1) {
			int m=(y+tx)/2;
			if(AA(x,m)<=A) tx=m;
			else y=m;
		}
		ret += (y-1-x)*2;
	}
	return ret;
}

