    [&](D x)->Int{
      auto calc=[&](Int a,Int b)->D{return ss[a+b]*ss[a]*ss[b];};
      Int cnt=0;
      // seisankaku (a = b = c)
      if(n%3==0){
        Int e=n/3;
        if(calc(e,e)<=x) cnt+=e;
